function chunkArrayInGroups(arr, size) {
  /*Write a function that splits an array (first 
  argument) into groups the length of size (second 
  argument) and returns them as a two-dimensional 
  array.*/

  let holdingArr=[];
  let twoDArr=[];
  let pos=0;

  //loops over each element in array
  for (let i=0;i<arr.length;i++) {
    //pushes each element to holding array
    //stops after size iterations
    for (let j=0;j<size;j++) {
      //prevents pushing empty values
      if (arr.length > pos) {
        holdingArr.push(arr[pos]);
        pos++;
      }
    }
    //prevents pushing empty values
    if (holdingArr.length>0) {
      twoDArr.push(holdingArr);
    }
    //resets holding array
    holdingArr=[];
  }

  return twoDArr;

}

chunkArrayInGroups(["a", "b", "c", "d"], 2);
