function mutation(arr) {
  /*Return true if the string in the first element of the array contains all of 
  the letters of the string in the second element of the array.*/
  let first = arr[0].toLowerCase().split("");
  let second = arr[1].toLowerCase().split("");
  let result = true;

  //checks first for each element in second
  //sets result to false if one isn't found
  for (let i=0;i<second.length;i++) {
    if (!first.includes(second[i])) {
      result=false;
    }
  }

  //result is true by default, unless changed by loop
  return result;
}

mutation(["hello", "hey"]);
