function palindrome(str) {
  var reduced = str.toLowerCase();
  console.log("reduced: " + reduced);
  var reduced = reduced.replace(/[*,./;':"!@#$%^&)(\-_+=\s]/g, "");

  var reversed = reduced.split(""); //splits original
  var original = reduced; //same value


  reversed = reversed.reverse().join(""); //reverses split
  console.log("reversed:" + reversed);
  console.log("original:" + original);
  if (reversed == original) { //compares the two
    return true;
  }
  return false;
}



palindrome("eye");
