function translatePigLatin(str) {
  /*Translate the provided string to pig latin.

  Pig Latin takes the first consonant (or consonant cluster) of an English word, 
  moves it to the end of the word and suffixes an "ay".

  If a word begins with a vowel you just add "way" to the end.

  Input strings are guaranteed to be English words in all lowercase.*/

  //check if starts with vowel or no vowels
  if (str[0].match(/[aeiou]/)) {
    console.log(str+'way');
    return str+'way';
  } else if (str.search(/[aeiou]/) === -1) {
    return str+'ay';
  }

  /*solve with a for loop
  let pigEnd = str[0];

  //loop through str for consonants
  let vowel = false;
  let i=1;
  while (!vowel) {
    //if next char is not a vowel, inc iterator, add cons to pigEnd
     if (str[i].match(/[^aeiou]/)) {
       pigEnd+=str[i];
       console.log(pigEnd);
       i++;
     } else {
       vowel=true;
       pigEnd=pigEnd+'ay'
     }
  }  

  //cut off str at the iterator found in while loop above
  return str.slice(i)+pigEnd;*/

  //solve with search function
  //find the first vowel
  let place = str.search(/[aeiou]/);

  //save the consonant or consonant cluster and add an 'ay'
  let pigEnd = str.slice(0, place)+'ay';

  //remove the consonant or consonant cluster and add the ending
  return str.slice(place).concat(pigEnd);


}

translatePigLatin("consonant");
