function frankenSplice(arr1, arr2, n) {

  /*Use the array methods slice and splice to copy
  each element of the first array into the second
  array, in order.

  Begin inserting elements at index n of the second 
  array.

  Return the resulting array. The input arrays should 
  remain the same after the function runs.*/

  //store first part in newArr
  let newArr=arr2.slice(0,n);

  //push each element in other array to newArr
  for (let i=0; i<arr1.length;i++) {
    newArr.splice(n+i,0,arr1[i]);
  }

  //store end part in endArr
  let endArr = arr2.slice(n-arr2.length);

  //push each remaining element to newArr
  for (let x=0;x<endArr.length;x++) {
    newArr.push(endArr[x]);
  }

  //return newArr
  return(newArr);
}

frankenSplice([1, 2, 3], [4, 5, 6], 1);
