function sumAll(arr) {
  /*We'll pass you an array of two numbers. Return 
  the sum of those two numbers plus the sum of all 
  the numbers between them.*/

  //sort least to greatest
  arr.sort(function(a, b){return a-b});
  let total=0;

  //add next number up to and including arr[1]
  for (let i=arr[0]; i<=arr[1];i++) {
    total+=i;
  }

  return total;
}

sumAll([1, 4]);
