function titleCase(str) {
  let arr = str.split(" ");
  let tempStr;
  let arrFinal=[];

  for (let i=0;i<arr.length;i++) {
    tempStr=arr[i][0].toUpperCase() + arr[i].slice(1,arr[i].length).toLowerCase();
    arrFinal.push(tempStr);
  }

  arrFinal = arrFinal.join(" ");

  console.log(arrFinal);
  return arrFinal;
}

