function getIndexToIns(arr, num) {
  /* Return the lowest index at which a value (second 
  argument) should be inserted into an array (first 
  argument) once it has been sorted. The returned 
  value should be a number.*/

  let result;

  //sort least to greatest
  arr.sort(function(a, b){return a-b});

  //functions for findIndex()
  function checkGreaterThan(val) {
    return val >= num;
  }

  function checkLessThan(val) {
    return val <= num;
  }  

  result= arr.findIndex(checkGreaterThan);

  //if findIndex() worked, return result
  //else, check for a value that is greater than all others
  if (result > -1) {
    return result;
  } else {
    result = arr.every(checkLessThan);
  }

  //if every() is true, return length of array since value is > than all values in array
  //else return 0
  if (result) {
    return arr.length;
  } else {
    return 0;
  }

}



