function whatIsInAName(collection, source) {
  // Make a function that looks through an array of objects (first argument) and returns an array of 
  // all objects that have matching name and value pairs (second argument). Each name and value pair 
  // of the source object has to be present in the object from the collection if it is to be included 
  // in the returned array.
  var arr=[];
  var tracker=true;
  var sourceProps = Object.getOwnPropertyNames(source);
  console.log(sourceProps);

  for (let i=0; i<collection.length;i++) {
    for (let j=0;j<sourceProps.length; j++) {
      if (collection[i].hasOwnProperty(sourceProps[j]) && tracker) {
        if (collection[i][sourceProps[j]]===source[sourceProps[j]] && tracker) {
          tracker=true;
        } else {
          tracker=false;
        }
      } else {
        tracker=false;
      }
    }
    if (tracker) {
      arr.push(collection[i]);
    }
    tracker=true;
  }

  console.log(arr);
  return arr;
}

whatIsInAName([{ first: "Romeo", last: "Montague" }, { first: "Mercutio", last: null }, { first: "Tybalt", last: "Capulet" }], { last: "Capulet" });
